import React, { useState } from "react";
import "./intro.css";
import bg from "../../assets/bg.png";

const Intro = () => {
    // State to track whether the additional details are visible or not
    const [showDetails, setShowDetails] = useState(false);

    // Function to toggle the visibility of additional details
    const toggleDetails = () => {
        setShowDetails(!showDetails);
    };

    return (
        <section id="intro">
            <div className="introContent">
                <span className="hello">Hello</span>
                <span className="introText">I'm <span className="introName">Priyanshu</span><br />Website Developer </span>
                <p className="introPara">
                    I am a skilled web designer with experience in<br /> creating visually appealing and user-friendly websites.
                    {showDetails && (
                        <>
                            <br />
                            I specialize in MERN stack development, which includes:
                            <ul>
                                <li>MongoDB - for database management</li>
                                <li>Express.js - for building the backend</li>
                                <li>React.js - for building the frontend</li>
                                <li>Node.js - for server-side JavaScript execution</li>
                            </ul>
                            With expertise in these technologies, I can develop dynamic and responsive web applications.
                        </>
                    )}
                </p>
                {/* Button to toggle visibility of additional details */}
                <button className="Btn" onClick={toggleDetails}>
                   {showDetails ? "Hide Details" : "More Details ?"}
                </button>
            </div>
            <img className="bg" src={bg} alt="bg" />
        </section>
    );
};

export default Intro;
