import React, { useEffect, useState } from 'react';
import "./footer.css";

function Footer() {
  const [scrolled, setScrolled] = useState(0);

  const handleScroll = () => {
    const winScroll = document.body.scrollTop || document.documentElement.scrollTop;
    const height = document.documentElement.scrollHeight - document.documentElement.clientHeight;
    const scrolledPercentage = (winScroll / height) * 100;
    setScrolled(scrolledPercentage);
  };

  useEffect(() => {
    // Attach the scroll event listener when the component mounts
    window.addEventListener('scroll', handleScroll);

    // Detach the scroll event listener when the component unmounts
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []); // Empty dependency array means this effect runs only once, similar to componentDidMount

  return (
    <footer className="footer">
      Copyrigt &#169; 2023 Smith Williams. All right received.
      <div class="header">
        <h2>Scroll Indicator</h2>
        <div class="progress-container">
          <div class="progress-bar" id="myBar" style={{ width: `${scrolled}%`, height: '5px', background: 'green' }}></div>
        </div>
      </div>

      <div>content...</div>
    </footer>
  );
}

export default Footer;
