import React, { useState } from "react";
import "./intro.css";
import bg from "../../assets/bg.png";

const Intro = () => {
    const [showMoreDetails, setShowMoreDetails] = useState(false);

    const handleMoreDetailsClick = () => {
        setShowMoreDetails(!showMoreDetails);
    };

    return (
        <section id="intro">
            <div className="introContent">
                <span className="hello">Hello</span>
                <span className="introText">
                    I'm <span className="introName">Priyanshu</span><br />Website Developer
                </span>
                <p className="introPara">
                    I am a skilled web designer with experience in creating visually appealing and
                    user-friendly websites.{" "}
                    {showMoreDetails && (
                        <>
                            I specialize in the MERN stack (MongoDB, Express, React, Node.js) and
                            have a deep understanding of JavaScript, HTML, and CSS. I am also
                            experienced in front-end libraries such as Bootstrap and Material UI.
                        </>
                    )}
                </p>
                <button className="Btn" onClick={handleMoreDetailsClick}>
                    {showMoreDetails? "Hide Details" : "More Details"}
                </button>
            </div>
            <img className="bg" src={bg} alt="bg" />
        </section>
    );
};

export default Intro;
