import React, { useState, useEffect } from 'react';
import './works.css';

function Works() {
  const [videos, setVideos] = useState([
    'https://www.youtube.com/embed/VIDEO_ID_1?autoplay=1&mute=1',
    'https://www.youtube.com/embed/VIDEO_ID_2?autoplay=1&mute=1',
    'https://www.youtube.com/embed/VIDEO_ID_3?autoplay=1&mute=1',
    // Add more video links here, replacing 'VIDEO_ID' with actual YouTube video IDs
  ]);

  useEffect(() => {
    // Handle potential errors during video loading (optional)
  }, [videos]);

  return (
    <section className="works">
      <h2 className="workstitle">My Portfolio</h2>
      <span className="worksdesc">
        I take pride in paying attention to the smallest details and make sure that my work is pixel perfect. I am excited to bring my skills and experience to help businesses achieve their goals and create a strong online presence.
      </span>
      <div className="worksVideos"> {/* Updated class name */}
        {videos.map((videoUrl, index) => (
          <iframe
          key={index}
          src={videoUrl}
          frameBorder="0"
          allow="autoplay; encrypted-media"
          allowFullScreen
          title={`Portfolio Video ${index + 1}`}
          className="worksVideo"
        ></iframe>
        ))}
      </div>
      <button className="workBtn">See More</button>
    </section>
  );
}

export default Works;
