import React from "react";
import "./intro.css";
import bg from "../../assets/bg.png";

const Intro = () => {
    const email = "bhardwajpriyanshu300@gmail.com";
    let details = "As a full stack developer, I have faced many challenges while working with MERN stack. From setting up the development environment to deploying the application, there are numerous hurdles that I have overcome. However, with perseverance and dedication, I have mastered the MERN stack and can now create complex web applications with ease.";

    const handleHireMeClick = () => {
        if (window.confirm("Do you want to see more details about a full stack developer's struggle with MERN stack?")) {
            document.querySelector(".introPara").innerHTML = details;
        } else {
            window.location.href = `mailto:${email}`;
        }
    };

    return (
        <section id="intro">
            <div className="introContent">
                <span className="hello">Hello</span>
                <span className="introText">I'm <span className="introName">Priyanshu</span><br />Website Developer </span>
                <p className="introPara">I am a skilled web designer with experience in<br /> creating visually appealing and user-friendly websites.</p>
                <button className="Btn" onClick={handleHireMeClick}>
                   More Details?
                </button>

            </div>
            <img className="bg" src={bg} alt="bg" />
        </section>
    );
};

export default Intro;
